<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Alien Civilization Summary</title>
  <style>
    .bar {
      fill: steelblue;
    }
  </style>
  <script src="https://d3js.org/d3.v6.min.js"></script>
</head>
<body>
  <svg width="800" height="500"></svg>
  <script>
    const data = {
      "Phaestians": {
        "total_population": 1045,
        "peaceful_aspect": 5,
        "sociable_aspect": 1
      },
      "Hersilians": {
        "total_population": 550,
        "peaceful_aspect": 3,
        "sociable_aspect": 1
      },
      "Kaveks": {
        "total_population": 250,
        "peaceful_aspect": 1,
        "sociable_aspect": 5
      }
    };

    const margin = {top: 30, right: 30, bottom: 50, left: 50};
    const width = 800 - margin.left - margin.right;
    const height = 500 - margin.top - margin.bottom;

    const svg = d3.select("svg")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`);

    const x0 = d3.scaleBand()
      .domain(Object.keys(data))
      .range([0, width])
      .padding(0.2);

    const x1 = d3.scaleBand()
      .domain(["total_population", "peaceful_aspect", "sociable_aspect"])
      .range([0, x0.bandwidth()])
      .padding(0.05);

    const y = d3.scaleLinear()
      .domain([0, d3.max(Object.values(data), d => Math.max(d.total_population, d.peaceful_aspect * 250, d.sociable_aspect * 250))])
      .range([height, 0]);

    const xAxis = d3.axisBottom(x0);
    const yAxis = d3.axisLeft(y);

    svg.append("g")
      .attr("class", "x-axis")
      .attr("transform", `translate(0,${height})`)
      .call(xAxis);

    svg.append("g")
      .attr("class", "y-axis")
      .call(yAxis);

    const civilization = svg.selectAll(".civilization")
      .data(Object.entries(data))
      .join("g")
      .attr("class", "civilization")
      .attr("transform", d => `translate(${x0(d[0])},0)`);

    civilization.selectAll(".bar")
      .data(d => Object.entries(d[1]).map(e => ({ key: e[0], value: e[1] })))
      .join("rect")
      .attr("class", "bar")
      .attr("x", d => x1(d.key))
      .attr("y", d => y(d.value))
      .attr("width", x1.bandwidth())
      .attr("height", d => height - y(d.value));
  </script>
</body>
</html>
